import React, { useState } from 'react';

const ChatPage = () => {
    const [messages, setMessages] = useState([]); // State for storing chat messages
    const [newMessage, setNewMessage] = useState(''); // State for the current input

    // Function to handle the submission of a new message
    const handleSendMessage = () => {
        if (newMessage.trim()) {
            // Create a message object with text and sender
            const message = {
                text: newMessage,
                sender: true, // Indicates the message is sent by the current user
            };
            // Add the new message to the list of messages
            setMessages([...messages, message]);
            // Clear the input field
            setNewMessage('');
        }
    };

    // Function to handle keydown events in the input field
    const handleKeyDown = (event) => {
        // Check if the key pressed is Enter
        if (event.key === 'Enter') {
            event.preventDefault(); // Prevent form submission (if any)
            handleSendMessage(); // Send the message
        }
    };

    return (
        <div className="container mt-4">
            <h1 className="mb-4">Chat Page</h1>

            {/* Display chat messages */}
            <div className="border p-3 mb-3" style={{ maxHeight: '300px', overflowY: 'auto' }}>
                {messages.map((message, index) => (
                    <div
                        key={index}
                        className={`p-2 mb-2 ${
                            message.sender ? 'text-right' : 'text-left'
                        }`}
                    >
                        {/* Apply Bootstrap classes for styling */}
                        <div
                            className={`d-inline-block p-2 rounded ${
                                message.sender ? 'bg-primary text-white' : 'bg-light'
                            }`}
                        >
                            {message.text}
                        </div>
                    </div>
                ))}
            </div>

            {/* Chat input field */}
            <div className="input-group">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={handleKeyDown}
                    placeholder="Type your message..."
                    className="form-control"
                />
                <button
                    onClick={handleSendMessage}
                    className="btn btn-primary ml-2"
                >
                    Send
                </button>
            </div>
        </div>
    );
};

export default ChatPage;
