import React, { useState } from 'react';
import axios from 'axios';
import './ChatPage.css';

const ChatPage = () => {
    const [messages, setMessages] = useState([]); // State for storing chat messages
    const [newMessage, setNewMessage] = useState(''); // State for the current input

    // Function to handle the submission of a new message
    const handleSendMessage = async () => {
        if (newMessage.trim()) {
            // Add the user's message to the list of messages
            const userMessage = {
                text: newMessage,
                isUser: true, // Indicates the message is from the user
            };
            setMessages((prevMessages) => [...prevMessages, userMessage]);

            // Clear the input field
            setNewMessage('');

            // Send the user's message to the API and handle the response
            await sendUserMessageToAPI(newMessage);
        }
    };

    // Function to handle keydown events in the input field
    const handleKeyDown = (event) => {
        // Check if the key pressed is Enter
        if (event.key === 'Enter') {
            event.preventDefault(); // Prevent form submission (if any)
            handleSendMessage(); // Send the message
        }
    };

    // Function to send user message to the API and receive response
    const sendUserMessageToAPI = async (userMessage) => {
        try {
            // Make an API request to send the user's message and receive a response
            const response = await axios.post('http://localhost:8000/detibot/${encodeURIComponent(message)}', {
                message: userMessage,
            });

            // Add the API response to the list of messages
            const apiResponse = {
                text: response.data.reply, // Adjust this line according to your API response structure
                isUser: false, // Indicates the message is from the API
            };
            setMessages((prevMessages) => [...prevMessages, apiResponse]);
        } catch (error) {
            console.error('Error sending message to API:', error);
            // Optionally handle the error (e.g., display an error message to the user)
        }
    };

    return (
        <div className="container mt-4">
            <h1 className="mb-4">Chat Page</h1>

            {/* Display chat messages */}
            <div className="chat-container">
                {messages.map((message, index) => (
                    <div
                        key={index}
                        className={`message-wrapper ${message.isUser ? 'user-message' : 'api-message'}`}
                    >
                        <div
                            className={`message-bubble ${message.isUser ? 'user-bubble' : 'api-bubble'}`}
                        >
                            {message.text}
                        </div>
                    </div>
                ))}
            </div>

            {/* Chat input field */}
            <div className="input-group">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={handleKeyDown}
                    placeholder="Type your message..."
                    className="form-control"
                />
                <button
                    onClick={handleSendMessage}
                    className="btn btn-primary ml-2"
                >
                    Send
                </button>
            </div>
        </div>
    );
};

export default ChatPage;
